    template <> bool math1::Liste<double>::onlyHasStrictlyPositiveValue()
// =============================================================================
{
    bool res = true ;
    
    for (size_t i=1; i<=this->size(); i++)
        if ( (*this)(i)<=0.0)
            res = false ;
    
    return res;
}
