   double math1::ffa(int n)
// =============================================================================
{
	double x,xm1,xm2   ;
    x = 0.0 ;
	
	if (n==0 || n==1)	{
		x=1.0 ;
	}
	else if (n>2)	{
		xm2=1.0 ; xm1=1.0 ;
		for (int i=2; i<=n; i++)	{
			x = xm2*double(i) ;
			xm2 = xm1 ;
			xm1 = x   ;
		}
	}
	
	return x  ;	
}
