   cdouble math1::ipn (int n)
// =============================================================================
{
	cdouble z ;
	int modipn ;
	modipn = (abs(n)%4) ;
	
	switch (modipn)	{
		case 0:
			z = cdouble(1.0,0.0) ;
			break ;
		case 1:
			z = cdouble(0.0,1.0) ;
			break ;
		case 2:
			z = cdouble(-1.0,0.0) ;
			break ;
		case 3:
			z = cdouble(0.0,-1.0) ;
			break ;
	}
	
	if (n<0)
		z=cdouble(1.0,0.0)/z ;
	
	return z ;
}
