   dTableau math1::g_FactorialInArray (int n)
// =============================================================================
{
	dTableau res(0, n) ;
	res(0) = 1.0 ;
	
	for (int i=1; i<=n; i++){
		res(i) = res(i-1) *double(i) ;
		
	}
	return res ;
}
