   dTableau math1::g_LogFactorialInArray (int n)
// =============================================================================
{
	dTableau res(0, n) ;
	res(0) = 0.0 ;
	
	for (int i=1; i<=n; i++){
		res(i) = res(i-1) + log(double(i)) ;
		
	}
	return res ;
}

